CREATE DATABASE universe;
----------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
CREATE TABLE galaxy (galaxy_id SERIAL PRIMARY KEY, name VARCHAR(255) NOT NULL UNIQUE, distance INT NOT NULL, star_count INT NOT NULL, diameter NUMERIC(10,2), description TEXT, is_our_neighbour BOOLEAN NOT NULL, CONSTRAINT galaxy_distance_positive CHECK (distance >= 0), CONSTRAINT galaxy_star_count_positive CHECK (star_count > 0));

----------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
CREATE TABLE star (star_id SERIAL PRIMARY KEY, name VARCHAR(255) NOT NULL UNIQUE, age_million_years INT NOT NULL, planet_count INT NOT NULL, diameter NUMERIC(10,2), description TEXT, has_earthlike_planets BOOLEAN NOT NULL, is_visible BOOLEAN NOT NULL, galaxy_id INT NOT NULL REFERENCES galaxy(galaxy_id), CONSTRAINT star_age_positive CHECK (age_million_years > 0), CONSTRAINT star_planet_count_positive CHECK (planet_count >= 0));

----------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
CREATE TABLE planet (planet_id SERIAL PRIMARY KEY,name VARCHAR(255) NOT NULL UNIQUE, age_thousand_years INT NOT NULL, sort_order INT NOT NULL, diameter NUMERIC(10,2), description TEXT, has_life BOOLEAN NOT NULL, has_water BOOLEAN NOT NULL, star_id INT NOT NULL REFERENCES star(star_id), CONSTRAINT planet_age_positive CHECK (age_thousand_years > 0), CONSTRAINT planet_order_positive CHECK (sort_order > 0));

----------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
CREATE TABLE moon (moon_id SERIAL PRIMARY KEY,name VARCHAR(255) NOT NULL UNIQUE, age_thousand_years INT NOT NULL, sort_order INT NOT NULL,diameter NUMERIC(10,2), description TEXT, is_single BOOLEAN NOT NULL, is_orbiting BOOLEAN NOT NULL, planet_id INT NOT NULL REFERENCES planet(planet_id), CONSTRAINT moon_age_positive CHECK (age_thousand_years > 0), CONSTRAINT moon_order_positive CHECK (sort_order > 0));

----------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
CREATE TABLE meteor (meteor_id SERIAL PRIMARY KEY, name VARCHAR(255) NOT NULL UNIQUE, age INT NOT NULL, speed INT NOT NULL, diameter NUMERIC(10,2), description TEXT, is_dangerous BOOLEAN NOT NULL, has_water BOOLEAN NOT NULL, CONSTRAINT meteor_age_positive CHECK (age > 0), CONSTRAINT meteor_speed_positive CHECK (speed > 0));

----------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
